  {
    logos {
      _id
      name
      texts {
      text
      left
      top
      color
      fontSize
      backgroundColor
      borderColor
      borderRadius
      borderWidth
      padding
      margin
    }
    images {
      src
      left
      top
      height
      width
    }
      lastUpdate
    }
  }
`;

