import React, { Component, Fragment } from 'react';
import { Link } from 'react-router-dom';
import '../App.css';
import gql from 'graphql-tag';
import logo from '../img/Logo final.svg'
import plusIcon from '../img/plus_icon.svg'
import { Query } from 'react-apollo';

const GET_LOGOS = gql`
  {
    logos {
      _id
      name
      texts {
      text
      left
      top
      color
      fontSize
      backgroundColor
      borderColor
      borderRadius
      borderWidth
      padding
      margin
    }
    images {
      src
      left
      top
      height
      width
    }
      lastUpdate
    }
  }
`;

class HomeScreen extends Component {

    render() {
        return (
            <Query pollInterval={500} query={GET_LOGOS}>
                {({ loading, error, data }) => {
                    if (loading) return 'Loading...';
                    if (error) return `Error! ${error.message}`;

                    return (

                        <Fragment>
                            <header>
                                <div className="logo-container">
                                    <img className="logo" src={logo} alt="Logo" />
                                </div>
                                <nav>
                                    <ul className="nav-links">
                                        <li className="nav-link">Signed in as User101@gmail.com</li>
                                        <li className="nav-link"><div className="yellow">SETTINGS</div></li>
                                        <li className="nav-link"><div className="red">LOG OUT</div></li>
                                    </ul>
                                </nav>
                            </header>
                            <section>
                                <div className="control-container">
                                    <div id="menu">
                                        <div className="yellow" style={{
                                            padding: "1rem",
                                            fontSize: 24,
                                            textAlign: "center"
                                        }
                                        }>Recent Works</div>
                                        {data.logos.sort(function (logo1, logo2) {
                                            if (logo1.lastUpdate < logo2.lastUpdate) {
                                                return 1;
                                            }
                                            else if (logo1.lastUpdate === logo2.lastUpdate) {
                                                return 0;
                                            }
                                            return -1;
                                        }).map((logo, index) => (

                                            <ul id="logo_links" key={index}>
                                                <li className="logo_link">
                                                    <div>

                                                        {logo.name}<Link style={{ color: 'inherit', textDecoration: 'inherit' }} to={`/view/${logo._id}`}>
                                                            <button style={{ alignSelf: "right", flex: 1 }}>Edit</button>
                                                        </Link>

                                                    </div>
                                                </li>
                                            </ul>
                                        ))} <div>

                                        </div>
                                    </div>
                                    <Link style={{ color: 'inherit', textDecoration: 'inherit' }} id="add_logo_button" to="/create">
                                        <div id="create_new_button">
                                            Create New
                                        <img style={{ marginLeft: 10, marginBottom: 5, verticalAlign: "middle" }} src={plusIcon} alt="Plus Icon" />

                                        </div></Link>



                                </div>
                                <div className="workplace-container">

                                </div>
                            </section>
                            <footer>
                                <div className="footer_text">
                                    Powered by GologoLo.org
                                </div>
                            </footer>
                        </Fragment>


                    );
                }
                }
            </Query >
        );
    }
}

export default HomeScreen;
