import React, { Component } from 'react';
import '../App.css';
import { Stage, Text, Image, Layer } from 'react-konva';
import Konva from 'konva';

class PreviewCanvas extends Component {
    constructor(props) {
        super();
        console.log(props);
        this.parentRef = React.createRef();
    };

    renderCanvas = () => {
        const parentStyle = window.getComputedStyle(React.findDOMNode(this.refs.parentRef));
        console.log("parent ref " + this.parentStyle);
