        }
        return (<Stage width={window.innerWidth * 0.65} height={window.innerHeight * 0.65}>
            <Layer>
                <Text
                    text="Choose a logo to preview"
                    align="center"
                    x={window.innerWidth / 2}
                    y={window.innerHeight / 2}
                    draggable={true}
                    fontFamily="Lato"
                />
            </Layer>
        </Stage>);
    }

    render() {
        return (
            <div id="canvas_container">
                <div className="green" style={{
                    padding: "1rem",
                    fontSize: 24,
                    textAlign: "center"
                }
                }>Preview</div>
                <div id="preview_canvas" ref={this.parentRef}>{this.renderCanvas()}</div>
            </div>

        );

    }
}

export default PreviewCanvas;
