import React, { Component } from 'react';
import '../App.css';
import { Stage, Text, Image, Layer } from 'react-konva';
import Konva from 'konva';

class PreviewCanvas extends Component {
    constructor(props) {
        super();
        console.log(props);
    };

    renderCanvas = () => {
        if (this.props.selected) {

        }
        return (<Stage width={window.innerWidth} height={window.innerHeight}>
            <Layer>
                <Text text="Choose a logo to preview" align="center" x={20} y={20} />
            </Layer>
        </Stage>);
    }

    render() {
        return (
            <div id="canvas_container">
                <div className="green" style={{
                    padding: "1rem",
                    fontSize: 24,
                    textAlign: "center"
                }
                }>Preview</div>
                <div id="preview_canvas">{this.renderCanvas()}</div>
            </div>

        );

    }
}

export default PreviewCanvas;
