import React, { Component, Fragment } from 'react';
import { Link } from 'react-router-dom';
import '../App.css';
import gql from 'graphql-tag';
import { Query } from 'react-apollo';

const GET_LOGOS = gql`
  {
    logos {
      _id
      name
      texts {
      text
      left
      top
      color
      fontSize
      backgroundColor
      borderColor
      borderRadius
      borderWidth
      padding
      margin
    }
    images {
      src
      left
      top
      height
      width
    }
      lastUpdate
    }
  }
`;

class HomeScreen extends Component {

    render() {
        return (
            <Query pollInterval={500} query={GET_LOGOS}>
                {({ loading, error, data }) => {
                    if (loading) return 'Loading...';
                    if (error) return `Error! ${error.message}`;

                    return (

                        <Fragment>
                            <nav className="navbar_container">
                                <ul clasName="navbar_ul">
                                    <li className="logo"><Link><image sr /></Link> </li>
                                    <li><a href="#">About</a></li>
                                </ul>
                            </nav>
                            <h1 className="display-1">Recent Work</h1>
                            <div style={{ padding: 20 }}>{data.logos.sort(function (logo1, logo2) {
                                if (logo1.lastUpdate < logo2.lastUpdate) {
                                    return 1;
                                }
                                else if (logo1.lastUpdate === logo2.lastUpdate) {
                                    return 0;
                                }
                                return -1;
                            }).map((logo, index) => (
                                <ul key={index}
                                    style={{ cursor: "pointer", fontSize: 24 }}>
                                    <li className="display-4">
                                        <Link style={{ color: 'inherit', textDecoration: 'inherit' }} to={`/view/${logo._id}`}>{logo.name}</Link>
                                    </li>
                                </ul>
                            ))}</div>
                            <div>
                                <button className="btn btn-light" style={{ fontSize: 64, margin: 20 }}>
                                    <Link style={{ color: 'inherit', textDecoration: 'inherit' }} id="add_logo_button" to="/create">Add Logo</Link>
                                </button>
                            </div>
                        </Fragment>


                    );
                }
                }
            </Query >
        );
    }
}

export default HomeScreen;
