import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import '../App.css';
import gql from 'graphql-tag';
import { Query } from 'react-apollo';

const GET_LOGOS = gql`
  {
    logos {
      _id
      text
      lastUpdate
    }
  }
`;

class HomeScreen extends Component {

    render() {
        return (
            <Query pollInterval={500} query={GET_LOGOS}>
                {({ loading, error, data }) => {
                    if (loading) return 'Loading...';
                    if (error) return `Error! ${error.message}`;

                    return (
                        <div className="container">
                            <div className="row">
                                <div style={{ marginBlock: 5 }} className="card-panel col s12">
                                    <div className="col s4 card white ">
                                        <div div style={{ fontSize: 200, padding: 5, height: 300 }} className="card-title  valign-wrapper"><p className="blue-grey-text center text-darken-2 "><em>G0""+o</em></p></div>
                                    </div>
                                    <div className="col s4 card t">
                                        <div div style={{ fontSize: 150, padding: 5, height: 300 }} className="card-title center-align valign-wrapper"><p className="blue-grey-text text-darken-2 "><em>Logo</em></p></div>
                                    </div>
                                    <div className="col s4 card">
                                        <div div style={{ fontSize: 350, padding: 5, height: 300 }} className="card-title center-align valign-wrapper"><p className="blue-grey-text text-darken-2 ">Lo</p></div>
                                    </div>
                                </div>
                                <div className="col s8">
                                    <h3>Recent Work</h3>
                                    {data.logos.map((logo, index) => (
                                        <div key={index} className='home_logo_link'
                                            style={{ cursor: "pointer" }}>
                                            <button><Link to={`/view/${logo._id}`}>{logo.text}</Link></button>
                                        </div>
                                    ))}
                                </div>
                                <div className="col s4">
                                    <div id="home_banner_container">
                                        @todo<br /> List Maker
                                </div>
                                </div>

                            </div>
                            <div className="row">
                                <div>
                                    <button><Link id="add_logo_button" to="/create">Add Logo</Link></button>
                                </div>
                            </div>
                        </div>

                    );
                }
                }
            </Query >
        );
    }
}

export default HomeScreen;
