import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import '../App.css';
import gql from 'graphql-tag';
import { Query } from 'react-apollo';

const GET_LOGOS = gql`
  {
    logos {
      _id
      text
      lastUpdate
    }
  }
`;

class HomeScreen extends Component {

    render() {
        return (
            <Query pollInterval={500} query={GET_LOGOS}>
                {({ loading, error, data }) => {
                    if (loading) return 'Loading...';
                    if (error) return `Error! ${error.message}`;

                    return (
                        <div className="container bg-light align-middle">
                            <div className="row" >
                                <div className="col bg-dark text-white" style={{ paddingTop: 200 }}>
                                    <h1 className="display-1">Recent Work</h1>
                                    <div>{data.logos.sort(function (logo1, logo2) {
                                        if (logo1.lastUpdate < logo2.lastUpdate) {
                                            return 1;
                                        }
                                        else if (logo1.lastUpdate === logo2.lastUpdate) {
                                            return 0;
                                        }
                                        return -1;
                                    }).map((logo, index) => (
                                        <div key={index}
                                            style={{ cursor: "pointer", fontSize: 24 }}>
                                            <p className='display-3 bg-light ' style={{ color: "white" }}>
                                                <Link to={`/view/${logo._id}`}>{logo.text}</Link>
                                            </p>
                                        </div>
                                    ))}</div>
                                    <div>
                                        <button style={{ fontSize: 32 }}>
                                            <Link id="add_logo_button" to="/create">Add Logo</Link>
                                        </button>
                                    </div>
                                </div>
                                <div className="col-lg text-dark text-lg">
                                    <div className="display-1" style={{ fontSize: 250 }}>Go<br />loGo<br /> LO</div>
                                </div>

                            </div>



                        </div>

                    );
                }
                }
            </Query >
        );
    }
}

export default HomeScreen;
